INSERT INTO `tbl_product` VALUES ('84', 'FJ-18', '35', null, null, 'am-dun-sieu-toc-fujika-fj-18', 'ẤM ĐUN SIÊU TỐC FUJIKA FJ-18', 'assets/uploads/post/anh-chi-tiet-fj-18.jpg', '[\"assets\\/uploads\\/am-sieu-toc-fujika-fj-18-1-8l.jpg\",\"assets\\/uploads\\/anh-chi-tiet-st25-51.jpg\",\"assets\\/uploads\\/anh-chi-tiet-st25-41.jpg\",\"assets\\/uploads\\/anh-chi-tiet-st25-31.jpg\",\"assets\\/uploads\\/anh-chi-tiet-st25-21.jpg\",\"assets\\/uploads\\/anh-chi-tiet-st25-11.jpg\"]', '', '<p>ẤM ĐUN SIÊU TỐC FUJIKA FJ-18</p>\r\n\r\n<ul>\r\n <li>Chất liệu cao cấp</li>\r\n <li>Dung tích: 1.8 lít</li>\r\n <li>Công suất: 1500W</li>\r\n <li>Tiết kiêm điện năng</li>\r\n <li>Bảo hành: 12 tháng</li>\r\n</ul>\r\n', null, '119000', null, null, null, null, '2018-04-06 10:04:30', '2018-04-06 10:04:30', '100', 'ẤM ĐUN SIÊU TỐC FUJIKA FJ-18 có độ bền cao và mang lại vẻ sáng bóng đẹp mắt', 'trang bị chế độ tự động ngắt điện khi nước sôi và khi bình cạn nước, đảm bảo an toàn trong khi sử dụng cho bạn và cả gia đình. Ấm siêu tốc Fujika FJ18 bằng inox bền đẹp. Ấm siêu tốc Fujika FJ18 (1.8l)có khả năng đun sôi nước trong một khoảng thời gian rất', 'ẤM ĐUN SIÊU TỐC', null, null, null, null, null, null, '1');
INSERT INTO `tbl_product` VALUES ('91', 'FJ-80N', '62', null, null, 'quat-suoi-fujika-fj-80n', 'QUẠT SƯỞI FUJIKA FJ-80N', 'assets/uploads/post/Anh-dai-dien-80N.jpg', '[\"assets\\/uploads\\/anh-chi-tiet-80N-1.jpg\",\"assets\\/uploads\\/anh-chi-tiet80N2.jpg\",\"assets\\/uploads\\/anh-chi-tiet-80N-11.jpg\",\"assets\\/uploads\\/anh-chi-tiet80N21.jpg\",\"\",\"\"]', '', '<h2>Thông số kỹ thuật của quạt sưởi Fujika FJ-80N</h2>\r\n\r\n<table border=\"1\" cellpadding=\"6\" cellspacing=\"0\" xss=removed>\r\n <tbody>\r\n  <tr>\r\n   <td> Hãng sản xuất</td>\r\n   <td> Fujika</td>\r\n  </tr>\r\n  <tr>\r\n   <td> Loại</td>\r\n   <td> Quạt sưởi</td>\r\n  </tr>\r\n  <tr>\r\n   <td> Model</td>\r\n   <td> FJ-80N</td>\r\n  </tr>\r\n  <tr>\r\n   <td> Điện năng</td>\r\n   <td> 220 - 240V/ 50-60Hz</td>\r\n  </tr>\r\n  <tr>\r\n   <td> Công suất</td>\r\n   <td> 400W/800W</td>\r\n  </tr>\r\n  <tr>\r\n   <td> Bảo hành</td>\r\n   <td> 12 tháng</td>\r\n  </tr>\r\n </tbody>\r\n</table>\r\n\r\n<p> </p>\r\n', null, '339000', null, null, null, null, '2018-04-06 19:36:45', '2018-04-06 19:36:45', '100', 'QUẠT SƯỞI FUJIKA FJ-80N Loại quạt sưởi này được thiết kế nhỏ, gọn, có quai treo, rất thuận tiện trong quá trình cầm nắm di chuyển. Dòng sản phẩm này phát nhiệt bằng bóng đèn Halogen. ', 'Quạt sưởi FJ-80N hoạt động dựa trên cơ chế chuyển hóa điện năng thành nhiệt năng, từ đó làm ấm không khí xung quanh nơi đặt quạt. Nhờ đó làm ấm thân thể người sử dụng. Vì thời gian làm ấm nhanh nên không cần phải chờ đợi, nhờ đó tiết kiệm điện năng, tiết ', 'QUẠT SƯỞI FUJIKA ', null, null, null, null, null, null, '1');
