INSERT INTO `tbl_product` VALUES ('162', 'SW-1628', '57', null, null, 'am-sieu-toc-sowun-sw-1628', 'ẤM SIÊU TỐC SOWUN SW-1628', 'assets/uploads/post/ảnh-đại-diện-mẫu1628.jpg', '[\"assets\\/uploads\\/anh-chi-tiet1628.jpg\",\"assets\\/uploads\\/anh-chi-tiet1628a.jpg\",\"assets\\/uploads\\/anh-chi-tiet16281.jpg\",\"assets\\/uploads\\/anh-chi-tiet1628a1.jpg\",\"assets\\/uploads\\/anh-chi-tiet16282.jpg\",\"assets\\/uploads\\/anh-chi-tiet1628a2.jpg\"]', '', '<p>THÔNG SỐ KỸ THUẬT ẤM SIÊU TỐC SW-1628 </p>\r\n\r\n<ul>\r\n <li>Tên sản phẩm: Ấm siêu tốc Sowun</li>\r\n <li>Model: SW S1628</li>\r\n <li>Hiệu điện thế: 220V</li>\r\n <li>Công suất: 1500W</li>\r\n <li>Dung tích: 1,7L</li>\r\n <li>Chất liệu: 2 lớp với lớp ngoài bằng nhựa và lớp trong là thép không gỉ 304</li>\r\n <li>Tiện ích: Mâm nhiệt phẳng (chống bám cặn) cấu tạo inox tốt nhất 18/10</li>\r\n <li>Thân ấm: Nhựa ABS, màu đỏ</li>\r\n <li>An toàn: Tự động ngắt khi nước sôi và khi cạn nước hay ấm qua nóng</li>\r\n <li>Đế xoay 360 độ: Có</li>\r\n <li>Hiển thị mực nước: Có</li>\r\n <li>Thương hiệu: Sowun</li>\r\n <li>Bảo hành: 12 tháng</li>\r\n</ul>\r\n', null, '390000', null, null, null, null, '2018-04-07 19:23:02', '2018-04-07 19:23:02', '100', 'ẤM SIÊU TỐC SOWUN SW-1628 THIẾT KẾ THEO KIỂU DÁNG CHÂU ẤU, AN TOÀN SỨC KHỎE, TIỆN ÍCH CHO MỌI NHÀ', 'ẤM SIÊU TỐC SOWUN SW-1628 VỚI NHỮNG đặc tính \r\nCó tốc độ đun sôi nhanh chóng chỉ cần một lúc sau hành động cắm điện và nhấn nút “ON”, nước tinh khiết sẽ sôi trong vài phút và bây giờ bạn chỉ việc rút dây cắm ra và sử dụng.\r\nKhi nước sôi ấm đun siêu tốc sẽ', 'ẤM SIÊU TỐC SOWUN SW-1628', null, null, null, null, null, null, '1');
