INSERT INTO `tbl_product` VALUES ('173', 'SW-6012', '52', null, null, 'noi-lau-dien-sowun-sw-6012', 'NỒI LẨU ĐIỆN SOWUN SW-6012', 'assets/uploads/post/ảnh-đại-diện-mẫu6012.jpg', '[\"assets\\/uploads\\/anh-chi-tiet6012.jpg\",\"assets\\/uploads\\/anh-chi-tiet6012A.jpg\",\"assets\\/uploads\\/anh-chi-tiet60121.jpg\",\"assets\\/uploads\\/anh-chi-tiet6012A1.jpg\",\"\",\"\"]', '', '<p>THÔNG SỐ KỸ THUẬT NỔI LẨU ĐIỆN ĐA NĂNG SOWUN SW-6012 </p>\r\n\r\n<ul>\r\n <li>Tên sản phẩm: Nồi lẩu điện đa năng Sowun</li>\r\n <li>Model: SW6012</li>\r\n <li>Công suất: 1500 W</li>\r\n <li>Dung tích: 5 L</li>\r\n <li>Chất liệu: hợp kim phủ đá.</li>\r\n <li>Quai cầm: chống nóng</li>\r\n <li>Điện áp: 220V – 50Hz</li>\r\n <li>Bảo hành: 12 tháng.</li>\r\n</ul>\r\n', null, '669000', null, null, null, null, '2018-04-09 18:11:52', '2018-04-09 18:11:52', '100', 'Nồi lẩu điện Sowun SW-6012 Đa chức năng nấu: Lẩu, canh, súp, chiên, xào, hấp, nướng; Kiểu dáng thiết kế chắc chắn; Điều chỉnh nhiệt độ từ 0°C-220°C Nắp kính trong suốt, chịu nhiệt cao; Thân nồi bằng Inox cao cấp chịu nhiệt; Lòng Nồi dày tráng men chống dí', 'Nồi lẩu điện Sowun SW-6012 với ưu điểm vượt trội:\r\nĐồ lẩu cắm điện đa năng Sowun SW6012 được nghiên cứu trên nền tảng công nghệ tối tân từ Hàn Quốc.\r\nNhằm cải thiện tính lâu bền trong sản phẩm.\r\nThiết bị nấu lẩu đa năng Sowun SW6012 sử dụng chất liệu hợp ', 'Nồi lẩu điện ', null, null, null, null, null, null, '1');
