INSERT INTO `tbl_product` VALUES ('193', 'SW-F183', '18', null, null, 'bep-tu-don-sowun-sw-f183', 'BẾP TỪ ĐƠN SOWUN SW-F183', 'assets/uploads/post/anhdaidienmau183.gif', '[\"assets\\/uploads\\/anhchitietmau183a.gif\",\"assets\\/uploads\\/anhchitietmau183a1.gif\",\"assets\\/uploads\\/anhchitietmau183b2.gif\",\"assets\\/uploads\\/anhchitietmau183a2.gif\",\"assets\\/uploads\\/anhchitietmau183b1.gif\",\"assets\\/uploads\\/anhchitietmau183b.gif\"]', '', '<h3>Thông số kỹ thuật của bếp từ đơn Sowun SW F183</h3>\r\n\r\n<ol>\r\n <li>Bếp điện từ đơn Sowun SW F183</li>\r\n <li>Mã sản phẩm: SW F183</li>\r\n <li>Màu sắc: Black & Sliver</li>\r\n <li>Số bếp nấu: 1 bếp</li>\r\n <li>Mẫu mã bếp: Hình chữ nhật</li>\r\n <li>Kích cỡ bề mặt bếp: 28 x 36 cm</li>\r\n <li>Chất lượng mặt bếp: Ceramic</li>\r\n <li>Bộ điều chỉnh: Phím bấm cơ</li>\r\n <li>Chủng loại nồi nấu: Chỉ nấu nồi có phần đáy hít từ</li>\r\n <li>Mức công suất nấu: 12 mức</li>\r\n <li>Công suất : 2000W</li>\r\n <li>Điện áp: 220V – 50Hz</li>\r\n <li>Hẹn giờ: Có</li>\r\n <li>Khóa an toàn: Có</li>\r\n <li>Xuất xứ: Trung Quốc</li>\r\n <li>Bảo hành: 12 tháng</li>\r\n</ol>\r\n', null, '799000', null, null, null, null, '2018-04-08 18:24:12', '2018-04-08 18:24:12', '100', 'BẾP TỪ ĐƠN SOWUN SW-F183 Với bề ngoài sang trọng hoa văn vô cùng bắt mắt. Gồm những đặc tính thông minh mà còn rất an toàn tới người sử dụng', 'ề ngoài bếp từ loại tốt Sowun SW F183 tinh tế tạo tiện nghi đẳng cấp cho khu bếp từng gia đình.\r\nSở hữu nhiều dạng bếp đun song hành, chị em có thể làm song hành cực nhiều món rút bớt lại số giờ nấu ăn.\r\nCùng với công suất mạnh đạt đến 2000W nấu ăn cực kỳ', 'BẾP TỪ ĐƠN SOWUN ', null, null, null, null, null, null, '1');
