INSERT INTO `tbl_product` VALUES ('208', 'SW-9223', '14', null, null, 'bep-gas-am-sowun-sw-9223', 'BẾP GAS ÂM SOWUN SW-9223', 'assets/uploads/post/ảnh-đại-diện-mẫu9223.jpg', '[\"assets\\/uploads\\/anh-chi-tiet9223.jpg\",\"\",\"\",\"\",\"\",\"\"]', '', '<h3>BẾP GA ÂM SOWUN SW-9223</h3>\r\n\r\n<p>- Hãng sản xuất: Sowun <br>\r\n– Bếp gas âm 2 lò nấu. <br>\r\n– Mặt kính chịu lực, chịu nhiệt cao cấp. <br>\r\n– Hệ thống đánh lửa bằng pin an toàn. <br>\r\n– Cảm ứng ngắt gas tự động, an toàn cho người sử dụng. <br>\r\n– PEP đồng siêu bền <br>\r\n– Họng inox SUS304 cao cấp, chống oxy hóa <br>\r\n– Lửa xanh tiết kiệm gas <br>\r\n– Kiểu dáng hiện đại, sang trọng. <br>\r\n- Kích thước mặt kính: 760x450x7mm <br>\r\n- Xuất xứ: Hàn Quốc <br>\r\n- Bảo hành: 12 tháng</p>\r\n\r\n<p> </p>\r\n', null, '2419000', null, null, null, null, '2018-04-09 18:34:44', '2018-04-09 18:34:44', '100', 'BẾP GAS ÂM SOWUN SW-9223 hình dáng thon mảnh, màu sắc thanh nhã, họa tiết nổi bật, thiết kế kiểu mới, rất tối giảm chi tiêu và an toàn khi vận hành', 'Bếp gas âm sowun sw-9223 ứng dụng vô số phương pháp tối ưu nhất tác động khiến giai đoạn chế biến biến thành nhẹ nhàng hơn bao giờ hết. Sở hữu bếp đun gas Sowun SW 9223 lắp đặt đem lại sự ưu việt tuyệt đối đi với bề ngoài nổi bật. Bếp gas âm Sowun SW-9223', 'BẾP GAS', null, null, null, null, null, null, '1');
