-- ----------------------------
-- Function structure for GetAncestry
-- ----------------------------
DROP FUNCTION IF EXISTS `GetAncestry`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` FUNCTION `GetAncestry`(GivenID INT) RETURNS varchar(1024) CHARSET latin1
    DETERMINISTIC
BEGIN
    DECLARE rv VARCHAR(1024);
    DECLARE cm CHAR(1);
    DECLARE ch INT;

    SET rv = '';
    SET cm = '';
    SET ch = GivenID;
    WHILE ch > 0 DO
        SELECT IFNULL(par_id,-1) INTO ch FROM
        (SELECT par_id FROM tbl_catalog WHERE id = ch) A;
        IF ch > 0 THEN
            SET rv = CONCAT(rv,cm,ch);
            SET cm = ',';
        END IF;
    END WHILE;
    RETURN rv;
END
;;
DELIMITER ;

